import { useState, useEffect } from "react";
import axios from "axios";
import choosenSong from "./songsList";
import spotifyToken from "./SpotifyToken";


export default function GuessPage() {
  const[song, setSong] = useState()
  const [token, setToken] = useState('')
  const todaysSong = choosenSong()
  const getUrl = async () => {
    const header = {
      headers: {
      Accept: "application/json", 
      "Content-Type": "application/json", 
      Authorization: `Bearer ${token}`
      }

    }
    await axios.get(`https://api.spotify.com/v1/search?q=$${todaysSong}%20theme&type=track`, header)
    .then(res => setSong(res.data.tracks.items[0]))
  }

  useEffect(() => {
    spotifyToken()
    .then(res => {setToken(res); console.log("response", res)})
    .then((potato) => {getUrl(); console.log("potato", potato)})
    .then((hola) => console.log("", hola))
  }, [])
  return (
    <div className="gamePage">
